import React, { Component } from "react";
import Header from "../../../component/header";
import { makeStyles } from "@material-ui/core/styles";
import { Button, TextField, withStyles } from "@material-ui/core";
import { findById } from "../../../action/units";
import UnitPageList from "../UnitPageList";
import styles from "./styles";
import { Container } from "semantic-ui-react";

export class UnitPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      id: match.params.id,
      name: ""
    };
  }

  componentDidMount() {
    const { id } = this.state;
    if (id) {
      const data = findById(id);
      this.setState({ ...data });
    }
  }

  onChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  onSubmit = event => {
    event.preventDefault();
    console.log(this.state);
  };
  render() {
    const { classes } = this.props;
    const { id, name } = this.state;
    return (
      <div>
        <Header>
          <Container>
            <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
              {id && (
                <div className={classes.formField}>
                  <TextField
                    id="id"
                    label="ID"
                    value={id}
                    name="id"
                    variant="filled"
                    inputProps={{ readOnly: true }}
                  />
                </div>
              )}
              <div className={classes.formField}>
                <TextField
                  id="name"
                  label="Name"
                  value={name}
                  name="name"
                  variant="filled"
                  onChange={this.onChange}
                  fullWidth
                />
              </div>
              <div className={classes}>
                <button
                  className={classes}
                  variant="contained"
                  className="ui red basic button"
                >
                  Save
                </button>
              </div>
            </form>
            {/* this is unit page with id: {JSON.stringify(this.state, 0, 2)}. */}
          </Container>
        </Header>
      </div>
    );
  }
}

export default withStyles(styles, { withTheme: true })(UnitPage);
