import React, { Component } from "react";
import "./Counter.css";
import { Button } from "reactstrap";
class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.intervalId = null;
    this.increment = this.increment.bind(this);
    this.decrement = this.decrement.bind(this);
    this.state = {
      value: this.props.value || 0
    };
  }

  componentDidMount() {
    this.intervalId = setInterval(() => this.increment(), 1000);
  }
  componentWillUnmount() {
    clearInterval(this.intervalId);
  }

  increment() {
    let value = this.state.value + 1;
    this.setState({ value: value <= this.props.max ? value : this.props.min });
  }
  decrement() {
    let value = this.state.value - 1;
    this.setState({ value: value >= this.props.min ? value : this.props.min });
  }

  render() {
    return (
      <div className="counter-button">
        <Button color="info" onClick={this.decrement}>
          -
        </Button>
        <span>{this.state.value}</span>
        <Button color="info" onClick={this.increment}>
          +
        </Button>
      </div>
    );
  }
}

// Counter.defaultProps = {
