import React, { Component } from "react";
import Header from "../../component/header";
import { Sidebar } from "semantic-ui-react";
import { Container } from "semantic-ui-react";
import "./Items.css";
class Items extends Component {
  render() {
    const item = [
      { id: "1", name: "Milk" },
      { id: "2", name: "Matcha" },
      { id: "3", name: "Tea" },
      { id: "4", name: "Coffee" }
    ];
    const renderItems = (items, index) => {
      return (
        <tr>
          <td>{items.id}</td>
          <td>{items.name}</td>
        </tr>
      );
    };

    return (
      <div>
        <Header />
        <Sidebar>
          <Sidebar.Pusher>
            <Container className="container-items">
              <br></br>
              <br></br>
              <br></br>
              <h3>Items</h3>
              <table class="ui red table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>NAME</th>
                  </tr>
                </thead>
                <tbody>{item.map(renderItems)}</tbody>
              </table>
              <hr></hr>

              <button class="ui orange button">
                <i class="sync icon"></i>Reload
              </button>
            </Container>
          </Sidebar.Pusher>
        </Sidebar>
        <Header />
      </div>
    );
  }
}

export default Items;
