import React, { Component } from "react";
import Header from "../../component/Header";
import { Table, Container, Button } from "reactstrap";

$(function() {
  const tbody = $("table tbody");

  function load() {
    tbody.empty();
    fetch("http://localhost:8080/items")
      .then(response => {
        if (!response.ok) {
          // $('#loading').modal('hide');
          const message = response.statusText;
          throw new Error(message || "Unknown Server Error.");
        }
        return response.json();
      })
      .then(jsonResponse => {
        if (jsonResponse.code != 0) {
          const message = jsonResponse.message;
          throw new Error(message || "Uknown Error (${jsonResponse.code}).");
        } else if (!jsonResponse.data) {
          throw new Error("Data is empty");
        }
        return jsonResponse.data;
      })
      .then(data => {
        const list = data.list;
        list.forEach(function(value) {
          let tr = $("<tr>");
          let a = $("<a>", {
            href: "transaction/" + value.id + ".html",
            text: value.id
          });
          tr.append($("<td>").append(a));
          tr.append($("<td>").text(value.amount));
          tr.append($("<td>").text(value.type));
          tr.append($("<td>").text(value.description));
          tr.append(
            $("<td>").append(
              $("<button>", {
                class: "btn btn-info",
                text: "Delete",
                click: () => remove(value.id)
              })
            )
          );
          tbody.append(tr);
        });

        // $('#loading').modal('hide')
      })
      .catch(error => console.log(error.message))
      .then(() => setTimeout(() => $("#loading").modal("hide"), 500));
  }
  $("reload-button").click(function() {
    load();
  });
});
class Items extends Component {
  render() {
    const item = [
      { id: "1", name: "Milk" },
      { id: "2", name: "Matcha" },
      { id: "3", name: "Tea" },
      { id: "4", name: "Coffea" }
    ];
    const renderItems = (items, index) => {
      return (
        <tr>
          <td>{items.id}</td>
          <td>{items.name}</td>
        </tr>
      );
    };

    return (
      <Container>
        <div>
          <Header />
          <h2>Items</h2>
          <Table className="table" responsive>
            <thead>
              <tr>
                <th>ID</th>
                <th>NAME</th>
              </tr>
            </thead>
            <tbody>{item.map(renderItems)}</tbody>
          </Table>
          <hr></hr>
          <Button className="reload-button" outline color="info">
            Reload
          </Button>{" "}
        </div>
      </Container>
    );
  }
}

export default Items;
