import {
  FIND_UNIT_REQUEST,
  FIND_UNIT_SUCCESS,
  FIND_UNIT_FAILURE,
  FIND_UNITS_REQUEST,
  FIND_UNITS_SUCCESS,
  FIND_UNITS_FAILURE
} from "./constants";
// import axios from "axios";
import { instanceAxios } from "../utils/apiUtil";

function sleep(delay, value) {
  return new Promise(function(resolve) {
    setTimeout(resolve, delay, value);
  });
}

export const findById = id => dispatch => {
  dispatch({ type: FIND_UNIT_REQUEST });
  instanceAxios
    .get(`units/${id}`)
    .then(data => sleep(2000, data))
    .then(data => data)
    .then(data => {
      dispatch(findByIdSucces);
    })
    .catch(error => {
      dispatch({
        type: FIND_UNIT_FAILURE,
        error: error
      });
    });
};

function findByIdSucces() {
  return {
    type: FIND_UNIT_SUCCESS,
    data: data
  };
}

export const findAll = () => dispatch => {
  dispatch({ type: FIND_UNITS_REQUEST });
  instanceAxios
    .get(`units`)
    .then(data => data)
    .then(data => {
      dispatch({
        type: FIND_UNITS_SUCCESS,
        data: data
      });
    });
};
