import React, { Component } from "react";
import Header from "../../../component/header";
import { TextField, withStyles, CircularProgress } from "@material-ui/core";
import { findStocksById, saveStock } from "../../../action/stocks";
import { connect } from "react-redux";
import { Container } from "semantic-ui-react";
import styles from "./styles";
import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";
import Autocomplete from "@material-ui/lab/Autocomplete";

export class StockPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      form: {
        id: match.params.id,
        item: {},
        quantity: 0,
        unit: {}
      },
      error: null
    };
  }
  class;
  onEdit() {
    this.props.history.push("stocks");
  }

  componentDidMount() {
    const { form } = this.state;
    if (form.id) {
      this.props.findStocksById(form.id);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    const { saveData, data, error, saveError, history } = this.props;

    if (prevProps.data != data) {
      this.setState({ form: data });
    } else if (prevProps.saveError !== saveError) {
      this.setState({ error: saveError });
    } else if (prevProps.error !== error) {
      this.setState({ error: error });
    } else if (saveData && prevProps.saveData !== saveData) {
      history.goBack();
    }
  }

  onChange = event => {
    const { name, value } = event.target;
    const { form } = this.state;
    this.setState({ form: { ...form, [name]: value } });
  };

  onSubmit = event => {
    event.preventDefault();
    this.props.saveStock(this.state.form);
    console.log(this.state);
  };

  onItemChange = (event, value) => {
    const { form } = this.state;
    this.setState({ form: { ...form, item: value } });
  };

  render() {
    const { classes, loading, saveError } = this.props;
    const { form, error } = this.state;
    const errorData = saveError?.data || {};
    return (
      <div>
        <Header>
          <Container>
            {!loading ? (
              <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
                {form.id && (
                  <div className={classes.formField}>
                    <TextField
                      id="id"
                      label="ID"
                      value={form.id}
                      name="id"
                      variant="filled"
                      fullWidth
                      inputProps={{ readOnly: true }}
                    />
                  </div>
                )}
                <div className={classes.formField}>
                  <Autocomplete
                    className={classes.select}
                    id="combo-box-demo"
                    fullWidth
                    autoHighlight
                    options={itemList}
                    value={form.item.name}
                    onChange={this.onChange}
                    getOptionSelected={(option, value) => option.id == value.id}
                    getOptionSelected={option => option.name}
                    style={{ width: 300 }}
                    renderInput={params => (
                      <TextField {...params} label="Item" variant="outlined" />
                    )}
                  />
                </div>
                <div className={classes.formField}>
                  <TextField
                    error={errorData.quantity}
                    helperText={
                      errorData.quantity ? errorData.quantity[0] : null
                    }
                    id="quantity"
                    label="Quantity"
                    value={form.quantity}
                    name="quantity"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.formField}>
                  <Autocomplete
                    id="combo-box-demo"
                    options={unitList}
                    value={form.unit.name}
                    onChange={this.onChange}
                    getOptionLabel={option => option.title}
                    style={{ width: 300 }}
                    renderInput={params => (
                      <TextField {...params} label="Unit" variant="outlined" />
                    )}
                  />
                </div>
                <div className={classes.buttonContainer}>
                  <button className="ui orange button" disabled={loading}>
                    <i className="save icon"></i>
                    Save
                  </button>
                </div>
              </form>
            ) : (
              <CircularProgress />
            )}
            <Snackbar
              open={this.state.error}
              autoHideDuration={3000}
              onClose={() => this.setState({ error: false })}
            >
              <Alert
                onClose={() => this.setState({ error: false })}
                elevation={6}
                variant="filled"
                severity="error"
              >
                {error?.message}
              </Alert>
            </Snackbar>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findStocksById.data,
  loading: state.findStocksById.loading || state.saveStock.loading,
  error: state.findStocksById.error,
  saveData: state.saveStock.data,
  saveError: state.saveStock.error
});

const mapDispatchToProps = {
  findStocksById,
  saveStock
};

const itemList = [
  { title: "Mi Sedaap Soto" },
  { title: "Mie Sedaap Goreng" },
  { title: "Indomie Goreng" },
  { title: "Indomie Goreng" },
  { title: "Indomie Goreng" }
];

const unitList = [
  { title: "Pcs" },
  { title: "Kg" },
  { title: "G" },
  { title: "G" },
  { title: "G" },
  { title: "G" }
];

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(StockPage)
);
