import {
  FIND_UNITS,
  FIND_UNIT_BY_ID
} from './constants'
export function findById(id) {
  return {
    type: FIND_UNIT_BY_ID,
    id: id
  };
}

export function findAll() {
  return {
    type: FIND_UNITS
  };
}
