import {
  FIND_UNIT_REQUEST,
  FIND_UNIT_SUCCESS,
  FIND_UNIT_FAILURE,
  FIND_UNITS_REQUEST,
  FIND_UNITS_SUCCESS,
  FIND_UNITS_FAILURE
} from "../action/constants";

const defaultState = { data: null, loading: false, error: null };

export function findUnitById(state = defaultState, action) {
  switch (action.type) {
    case "FIND_UNIT_BY_ID":
      for (const e of data) {
        if (e.id == action.id) {
          return e;
        }
      }
    default:
      return state;
  }
}

export function findUnits(state = defaultState, action) {
  switch (action.type) {
    case "FIND_UNITS":
      return data;
    default:
      return state;
  }
}
