const FIND_UNITS = "FIND_ITEMS";
const FIND_UNIT_BY_ID = "FIND_UNIT_BY_ID";

const data = [
  {
    id: 1,
    name: "Gram"
  },
  {
    id: 2,
    name: "Pieces"
  },
  {
    id: 3,
    name: "Kilogram"
  },
  {
    id: 4,
    name: "Liter"
  },
  {
    id: 5,
    name: "Kilobyte"
  },
  {
    id: 6,
    name: "Kilobyte"
  },
  {
    id: 7,
    name: "Kilobyte"
  }
];

function sleep(delay) {
  return new Promise(function(resolve) {
    setTimeout(resolve, delay);
  });
}

export function findByid(id) {
  let parseId = parseInt(id);
  for (const e of data) {
    console.log(e, parseId, e === parseId);

    if (e.id === parseId) {
      return e;
    }
  }
}
export async function findAll() {
  await sleep(3000);
  return data;
}
