import React, { Component } from "react";
import Header from "../../component/Header";
import {
  Table,
  Card,
  CardHeader,
  CardBody,
  Container,
  Row,
  Button
} from "reactstrap";
class Items extends Component {
  render() {
    const item = [
      { id: "1", name: "Milk" },
      { id: "2", name: "Matcha" },
      { id: "3", name: "Tea" },
      { id: "4", name: "Coffea" }
    ];
    const renderItems = (items, index) => {
      return (
        <tr>
          <td>{items.id}</td>
          <td>{items.name}</td>
        </tr>
      );
    };
    return (
      <div>
        <Header />
        <h2>Items</h2>
        <Table hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>NAME</th>
            </tr>
          </thead>
          <tbody>{item.map(renderItems)}</tbody>
        </Table>
        <hr></hr>
        <Button outline color="info">
          Reload
        </Button>{" "}
      </div>
    );
  }
}

export default Items;
