import React, { Component } from "react";
import Header from "../../../component/header";
import { TextField, withStyles, CircularProgress } from "@material-ui/core";
import { findAll } from "../../../action/transactions";
import { connect } from "react-redux";
import { Container } from "semantic-ui-react";
import styles from "./styles";
import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";

export class TransactionPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      id: match.params.id,
      name: "",
      amount: "",
      type: "",
      description: "",
      error: false,
      open: false
    };
  }
  class;
  onEdit() {
    this.props.history.push("units");
  }

  componentDidMount() {
    const { id } = this.state;
    if (id) {
      this.props.findTransactionsById(id);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    const { data, error } = this.props;
    if (prevProps.data != data) {
      this.setState({ ...data });
    } else if (error && prevProps.error !== error) {
      this.setState({ error: true });
    }
  }

  onChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  onSubmit = event => {
    event.preventDefault();
    console.log(this.state);
  };

  render() {
    const { classes, error, loading } = this.props;
    const { id, amount, type, description } = this.state;
    return (
      <div>
        <Header>
          <Container>
            {!loading ? (
              <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
                {id && (
                  <div className={classes.formField}>
                    <TextField
                      id="id"
                      label="ID"
                      value={id}
                      name="id"
                      variant="filled"
                      inputProps={{ readOnly: true }}
                    />
                  </div>
                )}
                <div className={classes.formField}>
                  <TextField
                    id="amount"
                    label="Amount"
                    value={amount}
                    name="amount"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.formField}>
                  <TextField
                    id="type"
                    label="Type"
                    value={type}
                    name="type"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.formField}>
                  <TextField
                    id="description"
                    label="Description"
                    value={description}
                    name="description"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.buttonContainer}>
                  <button className="ui orange button">
                    <i className="save icon"></i>
                    Save
                  </button>
                </div>
              </form>
            ) : (
              <CircularProgress />
            )}
            <Snackbar
              open={this.state.error}
              autoHideDuration={1000}
              onClose={() => this.setState({ error: false })}
            >
              <Alert
                onClose={() => this.setState({ error: false })}
                elevation={6}
                variant="filled"
                severity="error"
              >
                {error?.message}
              </Alert>
            </Snackbar>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findTransactionsById.data || [],
  loading: state.findTransactionsById.loading,
  error: state.findTransactionsById.error
});

const mapDispatchToProps = {
    findTransactionsById
};

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(TransactionPage)
);
