import React, { Component } from "react";
import Header from "../../../component/header";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import { findByid } from "../../../action/units";
import UnitPageList from "../UnitPageList";

export class UnitPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      id: match.params.id,
      name: null
    };
  }

  componentDidMount() {
    const { match } = this.props;
    const data = findByid(match.params.id);
    this.setState({ ...data });
  }

  onChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };
  render() {
    const { id, name } = this.state;
    return (
      <div>
        <Header>
          <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
            <div>
              <TextField
                id="id"
                label="ID"
                value={id}
                variant="filled"
                inputProps={{ readOnly: true }}
              />
            </div>
            <div>
              <TextField
                id="name"
                label="Name"
                value={name}
                variant="filled"
                onChange={this.onChange}
                fullWidth
              />
            </div>
          </form>
          {/* this is unit page with id: {JSON.stringify(this.state, 0, 2)}. */}
        </Header>
      </div>
    );
  }
}

export default UnitPage;
