import React, { Component } from "react";
import Header from "../../../component/header";
import { TextField, withStyles, CircularProgress } from "@material-ui/core";
import { findById, editUnitById } from "../../../action/units";
import { connect } from "react-redux";
import { Container } from "semantic-ui-react";
import styles from "./styles";
import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";
import "./Confirm.css";

export class UnitPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      id: match.params.id,
      name: "",
      error: false,
      open: false
    };
  }

  onEdit() {
    this.props.history.push("units");
  }

  componentDidMount() {
    const { id } = this.state;
    if (id) {
      this.props.findById(id);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    const { data, error } = this.props;
    if (prevProps.data != data) {
      this.setState({ ...data });
    } else if (error && prevProps.error !== error) {
      this.setState({ error: true });
    }
  }

  onChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  onSubmit = event => {
    event.preventDefault();
    console.log(this.state);
  };

  render() {
    const { classes, error, loading } = this.props;
    const { id, name } = this.state;
    return (
      <div>
        <Header>
          <Container>
            {!loading ? (
              <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
                {id && (
                  <div className={classes.formField}>
                    <TextField
                      id="id"
                      label="ID"
                      value={id}
                      name="id"
                      variant="filled"
                      inputProps={{ readOnly: true }}
                    />
                  </div>
                )}
                <div className={classes.formField}>
                  <TextField
                    id="name"
                    label="Name"
                    value={name}
                    name="name"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.buttonContainer}>
                  <button className="ui orange button" onClick={this.onEdit}>
                    <i class="save icon"></i>
                    Save
                  </button>
                </div>
              </form>
            ) : (
              <CircularProgress />
            )}
            <Snackbar
              open={this.state.error}
              autoHideDuration={1000}
              onClose={() => this.setState({ error: false })}
            >
              <Alert
                onClose={() => this.setState({ error: false })}
                elevation={6}
                variant="filled"
                severity="error"
              >
                {error?.message}
              </Alert>
            </Snackbar>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findUnitById.data,
  loading: state.findUnitById.loading || state.editUnitById.loading,
  error: state.findUnitById.error,
  editData: state.editUnitById.data,
  editError: state.editUnitById.error
});

const mapDispatchToProps = {
  findById,
  editUnitById
};

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(UnitPage)
);
