import React, { Component } from "react";
import Header from "../../../component/header";
import MUIDataTable from "mui-datatables";
import { YearSummary, deletedById } from "../../../action/transactions";
import { Container } from "semantic-ui-react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { CircularProgress, withStyles } from "@material-ui/core";
import styles from "./styles";
class TransactionListPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      total: 0,
      table: {
        sort: "asc",
        size: 20,
        page: 0,
        search: {
          name: ""
        }
      },
      error: null
    };
  }

  onReload = () => {
    this.reload();
  };

  reload = () => {
    this.props.YearSummary(this.state.table);
  };

  componentDidMount() {
    this.reload();
  }

  onChangeRowsPerPage = numbersOfRow => {
    const { table } = this.state;
    this.setState({ table: { ...table, size: numbersOfRow } });
  };

  componentDidUpdate(prevProps, prevState) {
    const { deleteData, deleteError, error, data } = this.props;
    const { table } = this.state;

    if (prevProps.data !== data) {
      this.setState({ data: data.entries, total: data.total });
    } else if (
      prevState.table !== table ||
      prevProps.deleteData !== deleteData
    ) {
      this.reload();
    } else if (deleteError && prevProps.deleteError !== deleteError) {
      this.setState({ error: deleteError });
    } else if (error && prevProps.error !== error) {
      this.setState({ error: error });
    }
  }

  onChangePage = currentPage => {
    const { table } = this.state;
    this.setState({ table: { ...table, search: { name: currentPage } } });
  };

  onSearchChange = searchText => {
    const { table } = this.state;
    this.setState({ table: { ...table, search: { name: searchText } } });
  };

  onColumnSortChange = (changedColumn, direction) => {
    const { table } = this.state;
    const sort = direction === "descending" ? "desc" : "asc";
    this.setState({ table: { ...table, sort } });
  };
  render() {
    const { classes, loading } = this.props;
    const { data, total, error, table } = this.state;

    const columns = [
      {
        name: "id",
        label: "ID",
        options: {
          sortDirection: table.sort
        }
      },
      {
        name: "amount",
        label: "Name",
        options: {
          sort: false
        }
      },
      {
        name: "type",
        label: "Type",
        options: {
          sort: false
        }
      }
    ];

    const options = {
      serverSide: true,
      page: table.page,
      filter: false,
      count: total,
      rowsPerPage: table.size,
      rowsPerPageOptions: [5, 10, 25, 50, 100],
      onChangeRowsPerPage: this.onChangeRowsPerPage,
      onChangePage: this.onChangePage,
      onSearchChange: this.onSearchChange,
      onColumnSortChange: this.onColumnSortChange,
      searchText: table.search.name,
      selectableRows: "single",
      textLabels: {
        body: {
          noMatch: loading ? (
            <CircularProgress />
          ) : (
            "Sorry, no matching records found"
          )
        }
      }
    };

    return (
      <div>
        <Header error={error}>
          <Container>
            <div style={{ boxSizing: "content-box" }}>
              <MUIDataTable
                error={error}
                title={"Summary Transaction By Year"}
                data={!loading ? data : []}
                columns={columns}
                options={options}
              />
            </div>
            <div className={classes.buttonContainer}>
              <button
                className="ui orange button"
                onClick={this.reload}
                disabled={loading}
              >
                <i className="sync icon"></i>Back
              </button>
            </div>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  deleteData: state.deletedTransactionsById.data,
  deleteError: state.deletedTransactionsById.error,
  data: state.findSummaryByYear.data || [],
  loading:
    state.findSummaryByYear.loading || state.deletedTransactionsById.loading,
  error: state.findSummaryByYear.error
});

const mapDispatchToProps = {
  YearSummary,
  deletedById
};

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(TransactionListPage)
);
