const FIND_UNITS = "FIND_UNITS";
const FIND_UNIT_BY_ID = "FIND_UNIT_BY_ID";


export function findById(id) {
  return {
    type: FIND_UNIT_BY_ID,
    id: id
  };
}

export function findAll() {
  return {
    type: FIND_UNITS
  };
}
