import React, { Component } from "react";
import Header from "../../component/header";
import MUIDataTable from "mui-datatables";
import { findAll } from "../../action/units";
import { Container } from "semantic-ui-react";
import { connect } from "react-redux";

const columns = [
  {
    id: "id",
    label: "ID",
    options: {
      filter: true,
      sort: true
    }
  },
  {
    name: "name",
    label: "Name",
    options: {
      filter: true,
      sort: true
    }
  }
];

class UnitPageList extends Component {
  constructor(props) {
    super(props);
  }
  onAdd = () => {
    this.props.history.push("units/add");
  };

  onDelete = (event, data) => {
    alert("You want delete " + data.length + " rows");
  };

  componentDidMount() {
    this.props.findAll();
  }

  onRowClick = rowMeta => {
    const { data } = this.props;
    const e = data.list[rowMeta.rowIndex];
    this.props.history.push(`/units/${e.id}`);
  };
  render() {
    const { classes, data, loading } = this.props;
    return (
      <div>
        <Header>
          <Container>
            <div className={classes}>
              <button
                onClick={this.onAdd}
                className={classes}
                className="ui red basic button"
              >
                New Unit
              </button>
            </div>
            <MUIDataTable
              title={"Units List"}
              data={data?.list || []}
              columns={columns}
              options={{
                onRowClick: this.onRowClick
              }}
            />
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findUnits.data || [],
  loading: state.findUnits.loading,
  error: state.findUnits.error
});

const mapDispatchToProps = {
  findAll
};

export default connect(mapStateToProps, mapDispatchToProps)(UnitPageList);
