import React, { Component } from "react";
import "./App.css";
import { Container } from "reactstrap";
export class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      name: 'didit'
    }
  }

  onChange = (e) => {

  }
  onSubmit = e => {
    e.preventDefault();
    console.log("data hasbeen submitted");
  };
  render() {
    return (
      <Container className="container-app">
        <div>
          <form onSubmit={this.onSubmit}>
            <label className="label-app">
              NAME:
              <input type="text" name="name" value={this.state.name} onChange={() => this.setState} />
            </label>
            <input type="submit" value="submit" />
          </form>
        </div>
      </Container>
    );
  }
}

export default App;

