import React, { Component } from "react";
import Header from "../../../component/header";
import { TextField, withStyles, CircularProgress } from "@material-ui/core";
import { findById, saveTransactions } from "../../../action/transactions";
import { connect } from "react-redux";
import { Container } from "semantic-ui-react";
import styles from "./styles";
import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";

export class TransactionPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      form: {
        id: match.params.id,
        amount: "",
        description: "",
        type: ""
      },
      error: false
    };
  }

  onEdit() {
    this.props.history.push("units");
  }

  componentDidMount() {
    const { form } = this.state;
    if (form.id) {
      this.props.findById(form.id);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    const { saveData, data, error, saveError, history } = this.props;

    if (prevProps.data != data) {
      this.setState({ form: data });
    } else if (prevProps.saveError !== saveError) {
      this.setState({ error: saveError });
    } else if (prevProps.error !== error) {
      this.setState({ error: error });
    } else if (saveData && prevProps.saveData !== saveData) {
      history.goBack();
    }
  }

  onChange = event => {
    const { name, value } = event.target;
    const { form } = this.state;
    this.setState({ form: { ...form, [name]: value } });
  };

  onSubmit = event => {
    event.preventDefault();
    this.props.saveTransactions(this.state.form);
    console.log(this.state);
  };

  render() {
    const { classes, loading, saveError } = this.props;
    const { form, error } = this.state;
    const errorData = saveError?.data || {};
    return (
      <div>
        <Header>
          <Container>
            {!loading ? (
              <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
                {form.id && (
                  <div className={classes.formField}>
                    <TextField
                      id="id"
                      label="ID"
                      value={form.id}
                      name="id"
                      variant="filled"
                      inputProps={{ readOnly: true }}
                    />
                  </div>
                )}
                <div className={classes.formField}>
                  <TextField
                    error={errorData.name}
                    helperText={errorData.name ? errorData.name[0] : null}
                    id="amount"
                    label="Amount"
                    value={form.amount}
                    name="amount"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.formField}>
                  <TextField
                    id="description"
                    label="Description"
                    value={form.description}
                    name="description"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.formField}>
                  <TextField
                    id="type"
                    label="Type"
                    value={form.type}
                    name="type"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <Autocomplete
        id="size-small-standard"
        size="small"
        options={top100Films}
        getOptionLabel={(option) => option.title}
        defaultValue={top100Films[13]}
        renderInput={(params) => (
          <TextField {...params} variant="standard" label="Size small" placeholder="Favorites" />
        )}
      />
                <div className={classes.buttonContainer}>
                  <button className="ui orange button">
                    <i class="reply all icon"></i>
                    Back
                  </button>
                  <button className="ui orange button" disabled={loading}>
                    <i className="save icon"></i>
                    Save
                  </button>
                </div>
              </form>
            ) : (
              <CircularProgress />
            )}
            <Snackbar
              open={this.state.error}
              autoHideDuration={3000}
              onClose={() => this.setState({ error: false })}
            >
              <Alert
                onClose={() => this.setState({ error: false })}
                elevation={6}
                variant="filled"
                severity="error"
              >
                {error?.message}
              </Alert>
            </Snackbar>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findTransactionsById.data,
  loading: state.findTransactionsById.loading || state.saveTransactions.loading,
  error: state.findTransactionsById.error,
  saveData: state.saveTransactions.data,
  saveError: state.saveTransactions.error
});

const mapDispatchToProps = {
  findById,
  saveTransactions
};

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(TransactionPage)
);
