  },
  {
    id: 7,
    name: "Beras"
  },
  {
    id: 8,
    name: "Susu"
  },
  {
    id: 9,
    name: "Kopi Hitam"
  },
  {
    id: 10,
    name: "Wedang Jahe"
  }
];

const unitList = [
  {
    id: 1,
    name: "Pcs",
    description: "Pieces"
  },
  {
    id: 2,
    name: "Pcs",
    description: "Pieces"
  },
  {
    id: 3,
    name: "Pcs",
    description: "Pieces"
  },
  {
    id: 4,
    name: "Pcs",
    description: "Pieces"
  },
  {
    id: 5,
    name: "Pcs",
    description: "Pieces"
  },
  {
    id: 6,
    name: "Pcs",
    description: "Pieces"
  },
  {
    id: 7,
    name: "L",
    description: "Liter"
  },
  {
    id: 8,
    name: "g",
    description: "Gram"
  },
  {
    id: 9,
    name: "Pack",
    description: "Pack"
  },
  {
    id: 10,
    name: "Pack",
    description: "Pack"
  }
];

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(StockPage)
);
