import React, { Component } from "react";
import Header from "../../component/header";
import { withRouter } from "react-router";
import MaterialTable from "material-table";
import { findAll } from "../../action/units";
import { Container } from "semantic-ui-react";
import AddIcon from "@material-ui/icons/Add";
class UnitPageList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: []
    };
  }
  onAdd = () => {
    this.props.history.push("units/add");
  };

  onDelete = (event, data) => {
    alert("You want delete " + data.length + " rows");
  };

  componentDidMount() {
    this.setState({ data: findAll() });
  }

  onRowClick = (event, data) => {
    this.props.history.push(`/units/${data.id}`);
  };
  render() {
    const { classes } = this.props;
    return (
      <div>
        <Header>
          <Container>
            <div className={classes}>
              <button
                onClick={this.onAdd}
                className={classes}
                class="ui red basic button"
              >
                New Item
              </button>
            </div>
            <MaterialTable
              columns={[
                { title: "id", field: "id" },
                { title: "name", field: "name" }
              ]}
              data={this.state.data}
              title="Unit"
              onRowClick={this.onRowClick}
              options={{
                selection: true
              }}
              actions={[
                {
                  tooltip: "Remove All Selected User",
                  icon: "delete",
                  onClick: this.onDelete
                }
              ]}
            />
          </Container>
        </Header>
        <div class="ui inverted pink vertical footer segment">
        <div class="ui center aligned container">
            <h4 class="ui inverted header">&copy; Copyright 2017 | All rights reserved | Blahhh</h4>
            <a href="https://www.facebook.com/"><i class="facebook square icon big"></i></a>
            <a href="https://twitter.com/"><i class="twitter square icon big"></i></a>
            <a href="https://www.linkedin.com/company/c"><i class="linkedin square icon big"></i></a>
        </div>
    </div>
      </div>
    );
  }
}

export default withRouter(UnitPageList);
