import React, { Component } from "react";
import Header from "../../component/header";
import MUIDataTable from "mui-datatables";
import { findAll } from "../../action/units";
import { Container } from "semantic-ui-react";
import { connect } from "react-redux";
import { CircularProgress } from "@material-ui/core";

const options = {};
const e = data.list[rowsDeleted.data[0].index];
class UnitPageList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      search: {},
      sort: "asc",
      size: 5,
      page: 0
    };
  }



  onReload = () => {
    this.props.findAll(this.state);
  };

  onAdd = () => {
    this.props.history.push("units/add");
  };

  onRowsDelete = rowsDeleted => {
    console.log("rowsDeleted : ", rowsDeleted);
    return false;
  };

  onRowClick = rowData => {
    this.props.history.push(`/units/${rowData[0]}`);
  };
  componentDidMount() {
    this.onReload();
  }

  onChangeRowsPerPage = numbersOfRow => {
    this.setState({ size: numbersOfRow });
  };

  componentDidUpdate(prevProps, prevState) {
    if (prevState !== this.state) {
      this.onReload();
    }
  }

  onChangePage = currentPage => {
    this.setState({ page: currentPage });
  };

  onSearchChange = serachText => {
    this.setState({ search: { name: serachText } });
  };

  onColumnSortChange = (changedColumn, direction) => {
    this.setState({ sort: direction == "descending" ? "desc" : "asc" });
  };

  onRowsDelete = rowsDeleted => {
    const { data } = this.props.data;
    console.log("id : ", e.id);
  };

  render() {
    const { classes, data, loading } = this.props;
    const dataList = !loading && data ? data.list : [];
    console.log(data);

    const columns = [
      {
        name: "id",
        label: "ID",
        options: {
          sortDirection: this.state.sort
        }
      },
      {
        name: "name",
        label: "Name",
        options: {
          sort: false
        }
      },
      {
        name: "description",
        label: "Description",
        options: {
          sort: false
        }
      }
    ];

    return (
      <div>
        <Header>
          <Container>
            <div className={classes}>
              <button
                onClick={this.onAdd}
                className={classes}
                className="ui orange button"
              >
                <i className="plus square outline icon"></i>
                New Unit
              </button>
            </div>
            <MUIDataTable
              title={"Units List"}
              data={dataList}
              columns={columns}
              options={{
                serverSide: true,
                page: this.state.page,
                filter: false,
                count: data?.total,
                onRowsDelete: this.onRowsDelete,
                onRowClick: this.onRowClick,
                rowsPerPage: this.state.size,
                rowsPerPageOptions: [1, 2, 3, 5, 10],
                onChangeRowsPerPage: this.onChangeRowsPerPage,
                onChangePage: this.onChangePage,
                onSearchChange: this.onSearchChange,
                onColumnSortChange: this.onColumnSortChange,
                searchText: this.state.search.name,
                selectableRows: "single",
                onRowsDelete: this.onRowsDelete,
                textLabels: {
                  body: {
                    noMatch: loading ? (
                      <CircularProgress />
                    ) : (
                      "Sorry, no matching records found"
                    )
                  }
                }
              }}
            />
            <button
              className="ui orange button"
              onClick={this.onReload}
              disabled={loading}
            >
              <i className="sync icon"></i>Reload
            </button>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findUnits.data || [],
  loading: state.findUnits.loading,
  error: state.findUnits.error
});

const mapDispatchToProps = {
  findAll
};

export default connect(mapStateToProps, mapDispatchToProps)(UnitPageList);
