import React, { Component } from "react";
import "./Counter.css";
class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 0
    };
  }

  componentDidMount(){

  }

  increment (){
    this.setState({value: this.state.value + 1})
  }
  decrement (){
    this.setState({value: this.state.value - 1})
  }


  render() {
    return (
      <div className="counter-button">
        <button>-</button>
        <span>{this.state.value}</span>
        <button>+</button>
      </div>
    );
  }
}

export default Counter;
