import React, { Component } from "react";
import "./Counter.css";
import { Button } from "reactstrap";
class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.intervalId = null;
    this.increment = this.increment.bind(this);
    this.decrement = this.decrement.bind(this);
    this.state = {
      value: this.props.value || 0
    };
  }

  // componentDidMount() {
  //   this.intervalId = setInterval(() => this.increment(), 1000);
  // }
  // componentWillUnmount() {
  //   clearInterval(this.intervalId);
  // }

  increment() {
    let value = this.state.value + 1;
    this.setState({ value: value <= this.props.max ? value : this.props.min });
  }
  decrement() {
    let value = this.state.value - 1;
    this.setState({ value: value >= this.props.min ? value : this.props.min });
  }

  render() {
    return (
      <div className="counter-button">
        <Button color="info" onClick={this.decrement} className="button-conter">
          -
        </Button>
        <span>
          <p>{this.state.value}</p>
        </span>
        <Button color="info" onClick={this.increment} className="button-conter">
          +
        </Button>
        <MaterialTable
          editable={{
            isEditable: rowData => rowData.name === "a", // only name(a) rows would be editable
            isDeletable: rowData => rowData.name === "b", // only name(a) rows would be deletable
            onRowAdd: newData =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  {
                    /* const data = this.state.data;
                        data.push(newData);
                        this.setState({ data }, () => resolve()); */
                  }
                  resolve();
                }, 1000);
              }),
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  {
                    /* const data = this.state.data;
                        const index = data.indexOf(oldData);
                        data[index] = newData;                
                        this.setState({ data }, () => resolve()); */
                  }
                  resolve();
                }, 1000);
              }),
            onRowDelete: oldData =>
              new Promise((resolve, reject) => {
                setTimeout(() => {
                  {
                    /* let data = this.state.data;
                        const index = data.indexOf(oldData);
                        data.splice(index, 1);
                        this.setState({ data }, () => resolve()); */
                  }
                  resolve();
                }, 1000);
              })
          }}
        />
      </div>
    );
  }
}

// Counter.defaultProps = {
//   min: 0,
//   max: 10
// }
// Counter.propTypes = {
//   min: PropTypes.number,
//   max: PropTypes.number
// }
export default Counter;
