import React, { Component } from "react";
import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Items from "./scenes/item";
import Units from "./scenes/unit";
import Transactions from "./scenes/transaction";
import Stocks from "./scenes/stock";
import Home from "./scenes/home";
import Header from "./component/header";
import Error from "./scenes/error";

const routes = [
  {
    path: "/",
    component: Home
  },
  {
    path: "/items",
    component: Items
  },
  {
    path: "/units",
    component: Units
  },
  {
    path: "/transactions",
    component: Transactions
  },
  {
    path: "/stocks",
    component: Stocks
  }
];
class App extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <Router>
        <Switch>
          {routes.map(route => (
            <Route path={route.path} exact component={route.component} exact={route.exact} />
          ))}
          <Route>
            <Error code={404} />
          </Route>
        </Switch>
      </Router>
    );
  }
}

export default App;
