import React, { Component } from "react";
import "./App.css";
import { Container, Badge, Table, Button } from "reactstrap";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
class App extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const item = [
      { id: "1", name: "Milk" },
      { id: "2", name: "Matcha" },
      { id: "3", name: "Tea" },
      { id: "4", name: "Coffea" }
    ];
    const renderItems = (items, index) => {
      return (
        <tr>
          <td>{items.id}</td>
          <td>{items.name}</td>
        </tr>
      );
    };
    return (
      <Container>
        <Router>
          <div>
            <h1>
              Inventory <Badge color="secondary">FORM</Badge>
            </h1>
            <nav>
              <ul>
                <li>
                  <Link to="/">Home</Link>
                </li>
                <li>
                  <Link to="/items">Items</Link>
                </li>
              </ul>
            </nav>
            <Switch>
              <Route className="items-app" path="/items" exact>
                <Items />
              </Route>
              <Route path="/" exact>
                <Home />
              </Route>
              <Route>
                <NotFound />
              </Route>
            </Switch>
            <div>
              <Table hover>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>NAME</th>
                  </tr>
                </thead>
                <tbody>{item.map(renderItems)}</tbody>
              </Table>
              <hr></hr>
              <Button outline color="info">
                Reload
              </Button>{" "}
            </div>
          </div>
        </Router>
      </Container>
    );
  }
}

function Home() {
  return <h2>Home</h2>;
}

function Items() {
  return <h2>Items</h2>;
}
function NotFound() {
  return <h2>NotFound</h2>;
}

export default App;
