import React, { Component } from "react";
import Header from "../../component/header";
import { Container } from "semantic-ui-react";
import "./Items.css";
class ItemListPage extends Component {
  render() {
    const item = [
      { id: "1", name: "Milk" },
      { id: "2", name: "Matcha" },
      { id: "3", name: "Tea" },
      { id: "4", name: "Coffee" }
    ];
    const renderItems = (items, index) => {
      return (
        <tr key={index}>
          <td>{items.id}</td>
          <td>{items.name}</td>
          <td>
            <button className="ui orange basic button" onClick={items.id}>
              <i className="edit icon"></i>
            </button>
            <button className="ui orange basic button">
              <i className="trash icon"></i>
            </button>
          </td>
        </tr>
      );
    };

    return (
      <div>
        <Header>
          <Container className="container-items">
            <br></br>
            <h3>Items</h3>
            <button className="ui orange button">
              <i className="plus square icon"></i>Add
            </button>
            <table className="ui red table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>NAME</th>
                  <th>ACTION</th>
                </tr>
              </thead>
              <tbody>{item.map(renderItems)}</tbody>
            </table>
            <hr></hr>

            <button className="ui orange button">
              <i className="sync icon"></i>Reload
            </button>
          </Container>
        </Header>
      </div>
    );
  }
}

export default ItemListPage;
