import React, { Component } from "react";
import Header from "../../component/header";
import { Card, Icon, Image, Menu } from "semantic-ui-react";
import { Link } from "react-router-dom";
import MyResponsiveBar from "./Chart";
import { ResponsiveBar } from "@nivo/bar";
class Home extends Component {
  render() {
    return (
      <div>
        <Header>
          <Card.Group>
            <Card style={{ width: "282px", height: "280px" }}>
              <Image
                src="https://www.berdesa.com/wp-content/uploads/2016/04/160418-Berdesa-2.jpg"
                wrapped
                ui={false}
              />
              <Card.Content>
                <Card.Header>ITEMS</Card.Header>
                <Card.Meta></Card.Meta>
                <Card.Description>All item in inventory.</Card.Description>
              </Card.Content>
              <Card.Content extra>
                <Menu.Item as={Link} to="/items">
                  <Icon name="location arrow" />
                  go to items
                </Menu.Item>
              </Card.Content>
            </Card>
            <Card style={{ width: "282px", height: "280px" }}>
              <Image
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTDocd9ug2yeUDKgXVhXgAqV00Go1LR36eUU_mP40FFEgXzcsMo&usqp=CAU"
                style={{ height: "165px" }}
                wrapped
                ui={false}
              />
              <Card.Content>
                <Card.Header>UNITS</Card.Header>
                <Card.Meta></Card.Meta>
                <Card.Description>All units in items.</Card.Description>
              </Card.Content>
              <Card.Content extra>
                <Menu.Item as={Link} to="/units">
                  <Icon name="location arrow" />
                  go to units
                </Menu.Item>
              </Card.Content>
            </Card>
            <Card style={{ width: "282px", height: "280px" }}>
              <Image
                style={{ height: "160px" }}
                src="https://cdn.datafloq.com/cache/blog_pictures/878x531/what-is-blockchain-transactions-smart-contracts.jpg"
                wrapped
                ui={false}
              />
              <Card.Content>
                <Card.Header>TRANSACTIONS</Card.Header>
                <Card.Description>Transactions inventory..</Card.Description>
              </Card.Content>
              <Card.Content extra>
                <Menu.Item as={Link} to="/transactions">
                  <Icon name="location arrow" />
                  go to transactions
                </Menu.Item>
              </Card.Content>
            </Card>
            <Card style={{ width: "282px", height: "280px" }}>
              <Image
                style={{ height: "165px" }}
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcR8zy-NvC8YNhbuSZPYXuh8jgEdFaiZjhDlA7URaaLWprWFr9L5&usqp=CAU"
                wrapped
                ui={false}
              />
              <Card.Content>
                <Card.Header>STOCKS</Card.Header>
                <Card.Meta></Card.Meta>
                <Card.Description>
                  All inventory in this application.
                </Card.Description>
              </Card.Content>
              <Card.Content extra>
                <Menu.Item as={Link} to="/stocks">
                  <Icon name="location arrow" />
                  go to stocks
                </Menu.Item>
              </Card.Content>
            </Card>
          </Card.Group>
        </Header>
        const MyResponsiveBar = ({data /* see data tab */}) => (
        <ResponsiveBar
          data={data}
          keys={["hot dog", "burger", "sandwich", "kebab", "fries", "donut"]}
          indexBy="country"
          margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
          padding={0.3}
          colors={{ scheme: "nivo" }}
          defs={[
            {
              id: "dots",
              type: "patternDots",
              background: "inherit",
              color: "#38bcb2",
              size: 4,
              padding: 1,
              stagger: true
            },
            {
              id: "lines",
              type: "patternLines",
              background: "inherit",
              color: "#eed312",
              rotation: -45,
              lineWidth: 6,
              spacing: 10
            }
          ]}
          fill={[
            {
              match: {
                id: "fries"
              },
              id: "dots"
            },
            {
              match: {
                id: "sandwich"
              },
              id: "lines"
            }
          ]}
          borderColor={{ from: "color", modifiers: [["darker", 1.6]] }}
          axisTop={null}
          axisRight={null}
          axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: "country",
            legendPosition: "middle",
            legendOffset: 32
          }}
          axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: "food",
            legendPosition: "middle",
            legendOffset: -40
          }}
          labelSkipWidth={12}
          labelSkipHeight={12}
          labelTextColor={{ from: "color", modifiers: [["darker", 1.6]] }}
          legends={[
            {
              dataFrom: "keys",
              anchor: "bottom-right",
              direction: "column",
              justify: false,
              translateX: 120,
              translateY: 0,
              itemsSpacing: 2,
              itemWidth: 100,
              itemHeight: 20,
              itemDirection: "left-to-right",
              itemOpacity: 0.85,
              symbolSize: 20,
              effects: [
                {
                  on: "hover",
                  style: {
                    itemOpacity: 1
                  }
                }
              ]
            }
          ]}
          animate={true}
          motionStiffness={90}
          motionDamping={15}
        />
        );
      </div>
    );
  }
}

export default Home;
