import {
  FIND_UNIT_REQUEST,
  FIND_UNIT_SUCCESS,
  FIND_UNIT_FAILURE,
  FIND_UNITS_REQUEST,
  FIND_UNITS_SUCCESS,
  FIND_UNITS_FAILURE
} from "../action/constants";

const defaultState = { data: null, loading: false, error: null };

export function findUnitById(state = defaultState, action) {
  switch (action.type) {
    case FIND_UNIT_REQUEST:
      return {
        ...state,
        loading: true
      };
    case FIND_UNIT_SUCCESS:
      return {
        data: action.data,
        loading: false
      };
    case FIND_UNIT_FAILURE:
      return {
        loading: false,
        error: action.error
      };
    default:
      return state;
  }
}

