import React, { Component } from "react";
import { Header, Icon, Image, Menu, Segment, Sidebar } from "semantic-ui-react";
import "./Header.css";
import { Link } from "react-router-dom";
import Items from "../../scenes/item";
import Units from "../../scenes/unit";
import Transactions from "../../scenes/transaction";
import Stocks from "../../scenes/stock";
import Home from "../../scenes/home";
import routes from "../../configs/routes"
const menus = [
  {
    path: "/",
    label: Home,
    path: "/items",
    label: Items,
    path: "/units",
    label: Units,
    path: "/transactions",
    label: Transactions,
    path: "/stocks",
    label: Stocks
  }
];
export default class SidebarExampleSidebar extends Component {
  state = { visible: false };

  handleSidebarHide = () => this.setState({ visible: false });
  handleSidebarToggle = () =>
    this.setState(prevState => ({ visible: !prevState.visible }));

  render() {
    const { visible } = this.state;
    return (
      <div>
        <div class="ui inverted menu">
          <Link class="active red item" onClick={this.handleSidebarToggle}>
            <div>
              <Icon name="tasks" />
            </div>
          </Link>
        </div>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation="overlay"
            icon="labeled"
            inverted
            onHide={this.handleSidebarHide}
            vertical
            visible={visible}
            width="thin"
          >
            <Menu.Item as="a">
              <Icon name="home" to="/"/>
              Home
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="folder open outline" />
              Items
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="folder open outline" />
              Units
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="folder open outline" />
              Transactions
            </Menu.Item>
            <Menu.Item as="a">
              <Icon name="folder open outline" />
              Stocks
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher>
            <Segment basic>
              <Header as="h3">Items</Header>
              <Image src="https://react.semantic-ui.com/images/wireframe/paragraph.png" />
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </div>
    );
  }
}
