import React, { Component } from "react";
import Header from "../../../component/header";
import { TextField, withStyles, CircularProgress } from "@material-ui/core";
import { findStocksById } from "../../../action/stocks";
import { connect } from "react-redux";
import { Container } from "semantic-ui-react";
import styles from "./styles";
import Snackbar from "@material-ui/core/Snackbar";
import Alert from "@material-ui/lab/Alert";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";

export class StockPage extends Component {
  constructor(props) {
    super(props);
    const { match } = this.props;
    this.state = {
      id: match.params.id,
      quantity: "",
      item: "",
      unit: "",
      error: false,
      open: false
    };
  }
  class;
  onEdit() {
    this.props.history.push("stocks");
  }

  componentDidMount() {
    const { id } = this.state;
    if (id) {
      this.props.findStocksById(id);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    const { data, error } = this.props;
    if (prevProps.data != data) {
      this.setState({ ...data });
    } else if (error && prevProps.error !== error) {
      this.setState({ error: true });
    }
  }

  onChange = event => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  onSubmit = event => {
    event.preventDefault();
    console.log(this.state);
  };

  render() {
    const { classes, error, loading } = this.props;
    const { id, quantity, item, unit } = this.state;
    return (
      <div>
        <Header>
          <Container>
            {!loading ? (
              <form noValidate autoComplete="off" onSubmit={this.onSubmit}>
                {id && (
                  <div className={classes.formField}>
                    <TextField
                      id="id"
                      label="ID"
                      value={id}
                      name="id"
                      variant="filled"
                      inputProps={{ readOnly: true }}
                    />
                  </div>
                )}
                <div className={classes.formField}>
                  <TextField
                    id="quantity"
                    label="Quantity"
                    value={quantity}
                    name="name"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <Autocomplete
                  id="combo-box-demo"
                  options={top100Films}
                  getOptionLabel={option => option.title}
                  style={{ width: 300 }}
                  renderInput={params => (
                    <TextField
                      {...params}
                      label="Combo box"
                      variant="outlined"
                    />
                  )}
                />
                <div className={classes.formField}>
                  <TextField
                    id="unit"
                    label="Unit"
                    value={unit.name}
                    name="unit"
                    variant="filled"
                    onChange={this.onChange}
                    fullWidth
                  />
                </div>
                <div className={classes.buttonContainer}>
                  <button className="ui orange button">
                    <i className="save icon"></i>
                    Save
                  </button>
                </div>
              </form>
            ) : (
              <CircularProgress />
            )}
            <Snackbar
              open={this.state.error}
              autoHideDuration={1000}
              onClose={() => this.setState({ error: false })}
            >
              <Alert
                onClose={() => this.setState({ error: false })}
                elevation={6}
                variant="filled"
                severity="error"
              >
                {error?.message}
              </Alert>
            </Snackbar>
          </Container>
        </Header>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  data: state.findStocksById.data,
  loading: state.findStocksById.loading,
  error: state.findStocksById.error
});

const mapDispatchToProps = {
  findStocksById
};

export default withStyles(styles, { withTheme: true })(
  connect(mapStateToProps, mapDispatchToProps)(StockPage)
);
